import {
    ALL_PRODUCTS_REQUEST,
    ALL_PRODUCTS_SUCCESS,
    ALL_PRODUCTS_FAIL,
    ADMIN_PRODUCTS_REQUEST,
    ADMIN_PRODUCTS_SUCCESS,
    ADMIN_PRODUCTS_FAIL,
    NEW_PRODUCT_REQUEST,
    NEW_PRODUCT_SUCCESS,
    NEW_PRODUCT_RESET,
    NEW_PRODUCT_FAIL,
    DELETE_PRODUCT_REQUEST,
    DELETE_PRODUCT_SUCCESS,
    DELETE_PRODUCT_RESET,
    DELETE_PRODUCT_FAIL,
    UPDATE_PRODUCT_REQUEST,
    UPDATE_PRODUCT_SUCCESS,
    UPDATE_PRODUCT_RESET,
    UPDATE_PRODUCT_FAIL,
    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_SUCCESS,
    PRODUCT_DETAILS_FAIL,
    NEW_REVIEW_REQUEST,
    NEW_REVIEW_SUCCESS,
    NEW_REVIEW_RESET,
    NEW_REVIEW_FAIL,
    GET_REVIEWS_REQUEST,
    GET_REVIEWS_SUCCESS,
    GET_REVIEWS_FAIL,
    DELETE_REVIEW_REQUEST,
    DELETE_REVIEW_SUCCESS,
    DELETE_REVIEW_RESET,
    DELETE_REVIEW_FAIL,
    CLEAR_ERRORS

} from '../constants/productConstants'
// thêm module nhà sản xuất

export const producersReducer = (state = { producers: [] }, action) => {
    switch (action.type) {
        case 'ALL_PRODUCERS_REQUEST':
        case 'ADMIN_PRODUCERS_REQUEST':
            return {
                loading: true,
                producers: []
            }

        case 'ALL_PRODUCERS_SUCCESS':
            return {
                loading: false,
                producers: action.payload.producers,
                producersCount: action.payload.producers,
                resPerPage: action.payload.resPerPage,
                filteredProducersCount: action.payload.filteredProducersCount
            }

        case 'ADMIN_PRODUCERS_SUCCESS':
            return {
                loading: false,
                producers: action.payload
            }

        case 'ALL_PRODUCERS_FAIL':
        case 'ADMIN_PRODUCERS_FAIL':
            return {
                loading: false,
                error: action.payload
            }

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null
            }

        default:
            return state;
    }
}

export const newProducerReducer = (state = { producer: {} }, action) => {
    switch (action.type) {
        case 'NEW_PRODUCER_REQUEST':
            return {
                ...state,
                loading: true
            }

        case 'NEW_PRODUCER_SUCCESS':
            return {
                ...state,
                loading: false,
                success: action.payload.success,
                producer: action.payload.producer
            }

        case 'NEW_PRODUCER_FAIL':
            return {
                ...state,
                error: action.payload
            }

        case 'NEW_PRODUCER_RESET':
            return {
                ...state,
                success: false
            }

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null
            }

        default:
            return state
    }
}

export const productReducer = (state = {}, action) => {
    switch (action.type) {

        case DELETE_PRODUCT_REQUEST:
        case UPDATE_PRODUCT_REQUEST:
            return {
                ...state,
                loading: true
            }

        case DELETE_PRODUCT_SUCCESS:
            return {
                ...state,
                loading: false,
                isDeleted: action.payload
            }

        case UPDATE_PRODUCT_SUCCESS:
            return {
                ...state,
                loading: false,
                isUpdated: action.payload
            }


        case DELETE_PRODUCT_FAIL:
        case UPDATE_PRODUCT_FAIL:
            return {
                ...state,
                error: action.payload
            }

        case DELETE_PRODUCT_RESET:
            return {
                ...state,
                isDeleted: false
            }

        case UPDATE_PRODUCT_RESET:
            return {
                ...state,
                isUpdated: false
            }

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null
            }

        default:
            return state
    }
}
