import { useState, useEffect, Fragment } from 'react'
import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import MetaData from '../layout/MetaData'
import Sidebar from './Sidebar'
import { newProducer, clearErrors } from '../../actions/producerActions'

const NewProducer = ({ history }) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [description, setDescription] = useState('');
    const [country, setCountry] = useState('');
    const [address, setAddress] = useState('');
    const [phone, setPhone] = useState('');

    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, success } = useSelector(state => state.newProducer);

    useEffect(() => {

        if (error) {
            alert.error(error);
            dispatch(clearErrors())
        }

        if (success) {
            history.push('/admin/producers');
            alert.success('Thêm nhà sản xuất thành công');
            dispatch({ type: 'NEW_PRODUCER_RESET' })
        }

    }, [dispatch, alert, error, success, history])

    const submitHandler = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set('name', name);
        formData.set('email', email);
        formData.set('country', country);
        formData.set('address', address);
        formData.set('description', description);
        formData.set('phone', phone);

        dispatch(newProducer(formData))
    }

    return (
        <Fragment>
            <MetaData title={'Thêm nhà sản xuất mới'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="wrapper my-5">
                            <form className="shadow-lg" onSubmit={submitHandler}>
                                <h2 className="mb-4">Thông tin nhà sản xuất</h2>

                                <div className="form-group">
                                    <label htmlFor="name_field">Tên:</label>
                                    <input 
                                        type="text" 
                                        id="name_field"
                                        className="form-control"
                                        value={name} 
                                        onChange={e => setName(e.target.value)} 
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="email_field">Email:</label>
                                    <input 
                                    type="email" 
                                    id="email_field" 
                                    className="form-control"
                                    value={email} 
                                    onChange={e => setEmail(e.target.value)} />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="description_field">Mô tả:</label>
                                    <textarea 
                                    type="text"
                                    id="description_field" 
                                    value={description} 
                                    onChange={e => setDescription(e.target.value)} />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="country_field">Quốc gia:</label>
                                    <input 
                                    type="text" 
                                    id="country_field" 
                                    value={country} 
                                    onChange={e => setCountry(e.target.value)} />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="address_field">Địa chỉ:</label>
                                    <input 
                                    type="text" 
                                    id="address_field" 
                                    value={address} 
                                    onChange={e => setAddress(e.target.value)} />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="phone_field">Số điện thoại:</label>
                                    <input 
                                    type="tel" 
                                    id="phone_field" 
                                    value={phone} 
                                    onChange={e => setPhone(e.target.value)} />
                                </div>
                                <button 
                                id="login_button"
                                type="submit"
                                className="btn btn-block py-3"
                                >Thêm mới
                                </button>

                            </form>
                        </div>
                    </Fragment>
                </div>
            </div>

        </Fragment>
    )
}

export default NewProducer;
